class Solution(object):
    def lengthOfLongestSubstring(self, s):
        """
        :type s: str
        :rtype: int
        """
        d = {}
        start = 0
        res = 0
        for i, ch in enumerate(s):
            if ch in d:
                res = max(res, i-start)
                start = max(start,d[ch]+1)
            d[ch] = i
        return max(res, len(s)-start)
    
    
if __name__ == '__main__':
    print(Solution().lengthOfLongestSubstring("abba"))

